    }

    try {
      // 更新选中的数据模型
      const updatedSchema = {
        ...selectedSchema,
        fields: fixedFields,
        keyIndexes: fixedKeyIndexes
      };

      // 调用 API 更新数据模型
      await putSchemasId(
        { id: selectedSchema.id! },
        {
          fields: updatedSchema.fields as any
        }
      );

      // 更新本地状态
      setSelectedSchema(updatedSchema);

      // 重新获取数据模型列表
      await fetchSchemas();

      message.success('自动修复完成！');
    } catch (error) {
      console.error('自动修复失败:', error);
      message.error('自动修复失败，请检查网络连接或手动修复');
