      // name: 代码的最后一部分（如：user_profile）
      // code: 完整的代码（如：enterprise:user_profile）
      // description: 原来的 name（如：企业用户信息表）
      const modelName = schemaData.code.split(':').pop() || schemaData.code;

      // 调用 API 创建数据模型
      await postSchemas({
        name: modelName,
