      case 'cola':
        return {
          name: 'cola',
          animate: true,
          refresh: 1,
          maxSimulationTime: 4000,
          ungrabifyWhileSimulating: false,
          fit: true,
          padding: 30,
          nodeDimensionsIncludeLabels: false,
          ready: function() {
            console.log('Cola 布局完成');
