          allConstIter: undefined
        };
      // case 'cose':
      //   return {
      //     name: 'cose',
      //     animate: true,
      //     animationDuration: 1000,
      //     nodeDimensionsIncludeLabels: true,
      //     fit: true,
      //     padding: 50,
      //     randomize: true,
      //     componentSpacing: 100,
      //     nodeRepulsion: (node: any) => 4000,
      //     nodeOverlap: 20,
      //     gravity: 80,
