  // 当 loading 变为 false 时初始化 cytoscape
  useEffect(() => {
    if (!loading) {
      console.log('loading 变为 false，开始初始化 cytoscape');
      setTimeout(() => {
        initCytoscape();
      }, 100);
    }
  }, [loading]);

  // 监听 drawerVisible 状态变化
  useEffect(() => {
    console.log('drawerVisible 状态变化:', drawerVisible);
    console.log('drawerTitle:', drawerTitle);
