                  {field.type === 'enum' && field.enumConfig && (
                    <div>
                      <Text type="secondary">枚举: {field.enumConfig.targetEnumCode}</Text>
                      <br />
                      <Text type="secondary">多选: {field.enumConfig.multiple ? '是' : '否'}</Text>
                    </div>
                  )}
                </Space>
              </div>
