import React, { useEffect, useState } from 'react';
import { Card, Spin, Alert, Button, Typography, Space, Badge } from 'antd';
import { CheckCircleOutlined, CloseCircleOutlined, LoadingOutlined } from '@ant-design/icons';
import { history } from '@umijs/max';
import { saveAuthInfo, User } from '../../auth';

const { Title, Text } = Typography;

interface ErrorResponse {
  success: false;
  message: string;
  details: string;
  code?: string;
}

interface SuccessResponse {
  success: true;
  token: string;
  user: {
    user_id: string;
    username: string;
    name: string;
    avatar: string;
    email: string;
    phone: string;
    gender: string;
    status: string;
    department_id: string | null;
  };
}

const SSOSuccess: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<ErrorResponse | null>(null);
  const [success, setSuccess] = useState<SuccessResponse | null>(null);

  useEffect(() => {
    // URL
    const checkForAuthData = () => {
      const urlParams = new URLSearchParams(window.location.search);
      const dataParam = urlParams.get('data');

      console.log('URL data param:', dataParam);

      if (dataParam) {
        try {
          // 
          const decodedData = decodeURIComponent(dataParam);
          const authData = JSON.parse(decodedData) as SuccessResponse;

          console.log('Parsed auth data:', authData);

          if (authData.success && authData.token && authData.user) {
            setSuccess(authData);
            // 
            // User
            const userData: User = {
              user_id: authData.user.user_id,
              username: authData.user.username,
              name: authData.user.name,
              avatar: authData.user.avatar,
              email: authData.user.email,
              phone: authData.user.phone,
              gender: authData.user.gender,
              status: authData.user.status,
              department_id: authData.user.department_id || undefined
            };

            console.log('Original user data:', authData.user);
            console.log('Converted user data:', userData);
            console.log('Avatar field:', userData.avatar);

            saveAuthInfo(authData.token, userData);

            // 
            setTimeout(() => {
              history.push('/');
            }, 2000);
          } else {
            setError({
              success: false,
              message: '',
              details: '',
              code: 'INVALID_AUTH_DATA'
            });
          }
        } catch (err) {
          console.error('Failed to parse auth data:', err);
          setError({
            success: false,
            message: '',
            details: '',
            code: 'PARSE_ERROR'
          });
        }
      } else {
        setError({
          success: false,
          message: '',
          details: 'SSO',
          code: 'NO_AUTH_DATA'
        });
      }

      setLoading(false);
    };

    // 
    setTimeout(checkForAuthData, 100);
  }, []);

  const handleGoHome = () => {
    history.push('/');
  };

  const handleRetry = () => {
    // URL
    window.location.reload();
  };

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        minHeight: '100vh',
        background: '#f5f5f5'
      }}>
        <Card style={{ width: 400, textAlign: 'center' }}>
          <Spin 
            indicator={<LoadingOutlined style={{ fontSize: 24 }} spin />} 
            size="large"
          />
          <Title level={4} style={{ marginTop: 16, marginBottom: 8 }}>

          </Title>
          <Text type="secondary">
            ...
          </Text>
        </Card>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        minHeight: '100vh',
        background: '#f5f5f5'
      }}>
        <Card style={{ width: 500 }}>
          <div style={{ textAlign: 'center', marginBottom: 24 }}>
            <CloseCircleOutlined 
              style={{ fontSize: 48, color: '#ff4d4f', marginBottom: 16 }} 
            />
            <Title level={3} style={{ color: '#ff4d4f', marginBottom: 8 }}>
              {error.message}
            </Title>
            <Text type="secondary" style={{ fontSize: 14 }}>
              {error.details}
            </Text>
          </div>

          <Alert
            message=""
            description={
              <div>
                <Text strong></Text> {error.code || 'UNKNOWN'}<br />
                <Text strong></Text> {error.message}<br />
                <Text strong></Text> {error.details}
              </div>
            }
            type="error"
            showIcon
            style={{ marginBottom: 24 }}
          />

          <Space style={{ width: '100%', justifyContent: 'center' }}>
            <Button type="primary" onClick={handleRetry}>

            </Button>
            <Button onClick={handleGoHome}>

            </Button>
          </Space>
        </Card>
      </div>
    );
  }

  if (success) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        minHeight: '100vh',
        background: '#f5f5f5'
      }}>
        <Card style={{ width: 400, textAlign: 'center' }}>
          <CheckCircleOutlined 
            style={{ fontSize: 48, color: '#52c41a', marginBottom: 16 }} 
          />
          <Title level={3} style={{ color: '#52c41a', marginBottom: 8 }}>

          </Title>
          <Text type="secondary" style={{ fontSize: 14 }}>
            {success.user.username}
          </Text>

          <Alert
            message=""
            description={
              <div style={{ textAlign: 'left' }}>
                <Text strong></Text> {success.user.name}<br />
                <Text strong></Text> {success.user.email}<br />
                <Text strong></Text> <Badge status="success" className='me-2' /> {success.user.status}
              </div>
            }
            type="success"
            // showIcon
            style={{ marginTop: 16, marginBottom: 16 }}
          />

          <Text type="secondary" style={{ fontSize: 12 }}>
            ...
          </Text>
        </Card>
      </div>
    );
  }

  return (
    <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      minHeight: '100vh',
      background: '#f5f5f5'
    }}>
      <Card style={{ width: 400, textAlign: 'center' }}>
        <Title level={3}></Title>
        <Text type="secondary">
          ...
        </Text>
      </Card>
    </div>
  );
};

export default SSOSuccess;

