import React, { useEffect, useState } from 'react';
import { Card, Button, message, Space } from 'antd';
import { request } from '@umijs/max';

const TestAuth: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState<any>(null);

  // API
  const testAuthApi = async () => {
    setLoading(true);
    try {
      const response = await request('/api/test/auth', {
        method: 'GET',
      });
      setResult(response);
      message.success('API');
    } catch (error) {
      console.error('API:', error);
      setResult(error);
      message.error('API');
    } finally {
      setLoading(false);
    }
  };

  // API
  const testPublicApi = async () => {
    setLoading(true);
    try {
      const response = await request('/api/test/public', {
        method: 'GET',
      });
      setResult(response);
      message.success('API');
    } catch (error) {
      console.error('API:', error);
      setResult(error);
      message.error('API');
    } finally {
      setLoading(false);
    }
  };

  // 401
  const testUnauthorized = async () => {
    setLoading(true);
    try {
      const response = await request('/api/test/unauthorized', {
        method: 'GET',
      });
      setResult(response);
      message.success('401API');
    } catch (error) {
      console.error('401API:', error);
      setResult(error);
      message.error('401API');
    } finally {
      setLoading(false);
    }
  };

  // 
  const testBusinessError = async () => {
    setLoading(true);
    try {
      const response = await request('/api/test/error', {
        method: 'GET',
      });
      setResult(response);
      message.success('API');
    } catch (error) {
      console.error('API:', error);
      setResult(error);
      message.error('API');
    } finally {
      setLoading(false);
    }
  };

  // token
  const testTokenStats = async () => {
    setLoading(true);
    try {
      const response = await request('/api/test/token-stats', {
        method: 'GET',
      });
      setResult(response);
      message.success('Token');
    } catch (error) {
      console.error('Token:', error);
      setResult(error);
      message.error('Token');
    } finally {
      setLoading(false);
    }
  };

  // tokenAPI
  const testTokenManagement = async () => {
    setLoading(true);
    try {
      const response = await request('/api/token-management/stats', {
        method: 'GET',
      });
      setResult(response);
      message.success('TokenAPI');
    } catch (error) {
      console.error('TokenAPI:', error);
      setResult(error);
      message.error('TokenAPI');
    } finally {
      setLoading(false);
    }
  };

  // token
  const testNoToken = async () => {
    setLoading(true);
    // token
    const originalToken = localStorage.getItem('token');
    localStorage.removeItem('token');

    try {
      const response = await request('/api/test/auth', {
        method: 'GET',
      });
      setResult(response);
      message.success('tokenAPI');
    } catch (error) {
      console.error('tokenAPI:', error);
      setResult(error);
      message.error('tokenAPI');
    } finally {
      // token
      if (originalToken) {
        localStorage.setItem('token', originalToken);
      }
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 24 }}>
      <Card title="" style={{ maxWidth: 800, margin: '0 auto' }}>
        <Space direction="vertical" style={{ width: '100%' }}>
          <Button 
            type="primary" 
            onClick={testAuthApi} 
            loading={loading}
            block
          >
            API
          </Button>

          <Button 
            onClick={testPublicApi} 
            loading={loading}
            block
          >
            API
          </Button>

          <Button 
            onClick={testUnauthorized} 
            loading={loading}
            block
          >
            401
          </Button>

          <Button 
            onClick={testBusinessError} 
            loading={loading}
            block
          >

          </Button>

          <Button 
            onClick={testTokenStats} 
            loading={loading}
            block
          >
            Token
          </Button>

          <Button 
            onClick={testTokenManagement} 
            loading={loading}
            block
          >
            TokenAPI
          </Button>

          <Button 
            danger
            onClick={testNoToken} 
            loading={loading}
            block
          >
            token
          </Button>

          {result && (
            <Card title="" size="small">
              <pre style={{ fontSize: 12, overflow: 'auto' }}>
                {JSON.stringify(result, null, 2)}
              </pre>
            </Card>
          )}
        </Space>
      </Card>
    </div>
  );
};

export default TestAuth;

