                />,
                <Popconfirm
                  key="delete"
                  title=""
                  description={` "${field.name}" `}
                  onConfirm={() => handleFieldDelete(index)}
                  okText=""
                  cancelText=""
                >
                  <Button
                    type="link"
                    shape='circle'
                    danger
                    icon={<DeleteOutlined />}
                  />
                </Popconfirm>,
              ]}
            >
              <List.Item.Meta
                title={
                  <Space>
                    <span>{field.name}</span>
                    {field.description && <span>({field.description})</span>}
                    <Tag color="blue">{field.type}</Tag>
                    {/*  -  */}
                    {/* {(field.type === 'uuid' || field.type === 'auto_increment') && field.isPrimaryKey && (
                      <Tag color="red">PK</Tag>
                    )} */}
                  </Space>
                }
                description={
                  <div style={{ marginTop: '4px' }}>
                    {/*  */}
                    {field.required && <Tag color="cyan" bordered={false}></Tag>}
                    {/*  */}
                    {getFieldIndexType(field.name) === 'primary' && (
                      <Tag color="red" bordered={false}></Tag>
                    )}
                    {getFieldIndexType(field.name) === 'unique' && (
                      <Tag color="orange" bordered={false}></Tag>
                    )}
                    {getFieldIndexType(field.name) === 'index' && (
                      <Tag color="green" bordered={false}></Tag>
                    )}
                    {getFieldIndexType(field.name) === 'fulltext' && (
                      <Tag color="purple" bordered={false}></Tag>
                    )}
                    {getFieldIndexType(field.name) === 'spatial' && (
                      <Tag color="blue" bordered={false}></Tag>
                    )}
                    {/*  */}
                    {field.type === 'string' && (field as API.StringField).length && (
                      <Tag color="cyan" bordered={false}>
                        VARCHAR({(field as API.StringField).length})
                      </Tag>
                    )}
                    {/*  */}
                    {field.type === 'text' && (
                      <Tag color="cyan" bordered={false}>
                        TEXT
                      </Tag>
                    )}
                    {/*  */}
                    {field.type === 'date' && (field as API.DateField).dateType && (
                      <Tag color="cyan" bordered={false}>{(field as API.DateField).dateType}</Tag>
                    )}
                    {/*  */}
                    {field.type === 'enum' && (field as API.EnumField).enumConfig && (
                      <>
                        <Tag color="cyan" bordered={false}>
                          : {getEnumDescription((field as API.EnumField).enumConfig?.targetEnumCode)}
                        </Tag>
                        {(field as API.EnumField).enumConfig?.multiple && (
                          <Tag color="purple" bordered={false}></Tag>
                        )}
                      </>
                    )}
                    {/*  */}
                    {field.type === 'relation' && (field as API.RelationField).relationConfig && (
                      <>
                        <Tag color="cyan" bordered={false}>
                          {getRelationTypeText(field as ExtendedRelationField)}
                        </Tag>
                        <Tag color="cyan" bordered={false}>
                          : {getTargetSchemaDescription((field as API.RelationField).relationConfig?.targetSchemaCode)}
                        </Tag>
                      </>
                    )}
                    {/*  */}
                    {field.type === 'media' && (field as API.MediaField).mediaConfig && (
                      <Tag color="cyan" bordered={false}>: {(field as API.MediaField).mediaConfig?.mediaType}</Tag>
                    )}
                    {/* API */}
                    {field.type === 'api' && (field as API.ApiField).apiConfig && (
                      <Tag color="cyan" bordered={false}>API: {(field as API.ApiField).apiConfig?.endpoint}</Tag>
                    )}
                    {/*  */}
                    {field.type === 'number' && (
                      <Tag color="cyan" bordered={false}></Tag>
                    )}
                  </div>
                }
              />
            </List.Item>
          )}
        />
      </div>
    );
  };

  // 
  const treeFilter = (inputValue: string, node: any) => {
    return node.title.toLowerCase().includes(inputValue.toLowerCase()) ||
           node.value.toLowerCase().includes(inputValue.toLowerCase());
  };

  // 
  const handleEnumChange: CascaderProps<EnumTreeNode>['onChange'] = (_, selectedOptions) => {
    if (selectedOptions && selectedOptions.length > 0) {
      const lastOption = selectedOptions[selectedOptions.length - 1];
      if (lastOption.isLeaf && lastOption.rawEnum) {
        const selectedEnum = lastOption.rawEnum;
        setSelectedEnumId(selectedEnum.code);
        setEnumDisplayText(`${selectedEnum.code}${selectedEnum.description || selectedEnum.name}`);
        fieldForm.setFieldValue('targetEnumCode', selectedEnum.code);
      }
    }
  };

  // 
  const enumFilter = (inputValue: string, path: DefaultOptionType[]) => {
    return path.some(option => {
      const label = option.label as string;
      const value = option.value as string;
      const searchStr = inputValue.toLowerCase();
      return (
        label.toLowerCase().includes(searchStr) ||
        value.toLowerCase().includes(searchStr)
      );
    });
  };

  // 
  const processTreeData = (nodes: SchemaTreeItem[], currentCode: string): SchemaTreeItem[] => {
    return nodes.map(node => {
      const newNode = { ...node };
      // 
      if (newNode.code === currentCode) {
        newNode.disabled = true;
      }
      // 
      if (newNode.children) {
        newNode.children = processTreeData(newNode.children, currentCode);
      }
      return newNode;
    });
  };

  // 
  useEffect(() => {
    if (editingField && editingField.type === 'enum' && editingField.enumConfig) {
      const selectedEnum = enums.find(e => e.code === editingField.enumConfig?.targetEnumCode);
      if (selectedEnum) {
        setSelectedEnumId(selectedEnum.code);
        setEnumDisplayText(`${selectedEnum.code}${selectedEnum.description || selectedEnum.name}`);
      }
    }
  }, [editingField, enums]);

  // 
  const handleEditField = (field: Field) => {
    setEditingField(field);

    // 
    setFieldType(field.type);

    // 
    const formData: any = {
      name: field.name,
      type: field.type,
      description: field.description,
      required: field.required,
      // isPrimaryKey: field.isPrimaryKey, // 
      length: field.length,
      dateType: field.dateType
    };

    // 
    switch (field.type) {
      case 'enum':
        if (field.enumConfig) {
          formData.targetEnumCode = field.enumConfig.targetEnumCode;
          formData.multiple = field.enumConfig.multiple;
          formData.defaultValues = field.enumConfig.defaultValues;
        }
        break;
      case 'relation':
        if (field.relationConfig) {
          formData.targetSchema = field.relationConfig.targetSchemaCode;
          formData.targetField = field.relationConfig.targetField;
          formData.cascadeDelete = field.relationConfig.cascadeDelete;
          formData.displayFields = field.relationConfig.displayFields;
          // multiplerelationType
          if (field.relationConfig.multiple) {
            formData.relationType = 'oneToMany';
          } else {
            formData.relationType = 'oneToOne';
          }
        }
        break;
      case 'media':
        if (field.mediaConfig) {
          formData.mediaConfig = field.mediaConfig;
        }
        break;
      case 'api':
        if (field.apiConfig) {
          formData.apiConfig = field.apiConfig;
        }
        break;
    }

    fieldForm.setFieldsValue(formData);
    setIsFieldModalVisible(true);
  };

  return (
    <div className="f-fullscreen">
      <Splitter style={{ height: "calc(100vh - 57px)" }}>
        <Splitter.Panel defaultSize="50%">
          <div className="f-header">
            <label className="fw-bold"></label>
            <Space>
              <Button
                type={isSyncMode ? "primary" : "link"}
                ghost={!isSyncMode}
                icon={<ExportOutlined />}
                onClick={handleSyncModeToggle}
              >
                {isSyncMode ? "" : ""}
              </Button>
              <Button
                type="link"
                ghost
                icon={<ApartmentOutlined />}
                onClick={() => {
                  navigate('/schema-graph');
                }}
              >

              </Button>
              <Button
                type="primary"
                ghost
                icon={<PlusOutlined />}
                onClick={() => {
                  schemaForm.resetFields();
                  setIsSchemaModalVisible(true);
                }}
              >

              </Button>
            </Space>

          </div>
          <div
            className="pos-relative overflow-y"
            style={{ height: `calc(100% - ${isSyncMode ? '106px' : '50px'})` }}
          >
            <div className="pb-4">
              <Table
                columns={schemaColumns}
                dataSource={schemaTreeData}
                rowKey="code"
                loading={loading}
                pagination={false}
                showHeader={false}
                size="small"
                expandable={{
                  expandedRowKeys,
                  onExpandedRowsChange: (expandedRows) => {
                    setExpandedRowKeys(expandedRows as string[]);
                  },
                  childrenColumnName: "children",
                  indentSize: 20,
                  expandIcon: ({ expanded, onExpand, record }) => {
                    // 
                    if (!record.children?.length) {
                      return null;
                    }
                    return (
                      <span 
                        onClick={(e) => {
                          e.stopPropagation();
                          onExpand(record, e);
                        }}
                        style={{ cursor: 'pointer' }}
                      >
                        {expanded ? <CaretDownOutlined style={{ fontSize: '12px', color: '#666', marginRight: '6px' }} /> : <CaretRightOutlined style={{ fontSize: '12px', color: '#999', marginRight: '6px', marginTop: '2px' }} />}
                      </span>
                    );
                  },
                }}
                onRow={(record: any) => ({
                  onClick: (e) => {
                    //  checkbox 
                    const target = e.target as HTMLElement;
                    const checkboxCell = target.closest('td');
                    if (checkboxCell && checkboxCell.cellIndex === 0 && isSyncMode) {
                      //  checkbox 
                      return;
                    }

                    if (record.children?.length) {
                      // /
                      const isExpanded = expandedRowKeys.includes(record.code);
                      if (isExpanded) {
                        setExpandedRowKeys(expandedRowKeys.filter(key => key !== record.code));
                      } else {
                        setExpandedRowKeys([...expandedRowKeys, record.code]);
                      }
                    } else {
                      // 
                      handleSchemaSelect(record as SchemaListItem);
                    }
                  },
                  className:
                    !record.children?.length && selectedSchema?.id === record.id
                      ? "ant-table-row-selected"
                      : "",
                  style: {
                    cursor: "pointer",
                  },
                })}
              />
            </div>
          </div>
          {/*  */}
          {isSyncMode && (
            <div 
              style={{ 
                position: 'fixed', 
                bottom: 0, 
                left: 0, 
                width: '100%',
                background: 'rgb(38 105 191 / 17%)',
                borderTop: '1px solid rgb(240 240 240 / 5%)',
                padding: '12px 16px',
                display: 'flex',
                justifyContent: 'space-between',
                backdropFilter: 'blur(10px)',
                alignItems: 'center',
                zIndex: 1000
              }}
            >
              <Space>
                <Checkbox
                  checked={isAllSelected}
                  indeterminate={isIndeterminate}
                  onChange={(e) => handleSelectAll(e.target.checked)}
                >

                </Checkbox>
                <span> {selectedRowKeys.length} </span>
              </Space>
              <Button
                type="primary"
                icon={<CloudDownloadOutlined />}
                onClick={handleDownloadORMClick}
                disabled={selectedRowKeys.length === 0}
              >
                Prisma
              </Button>
            </div>
          )}
        </Splitter.Panel>
        <Splitter.Panel>
          {
            selectedSchema && (
              <div className="f-header">
                <Space>
                  <span className='fw-bold'>{selectedSchema?.name}</span>
                  { selectedSchema?.description && <span className='me-1'>({selectedSchema?.description})</span> }
                  <span className='fw-bold me-2'> </span>
                  <SchemaValidator
                    fields={selectedSchema?.fields ?? []}
                    schemas={schemas}
                  />
                </Space>
                <Tooltip title="">
                  <Button
                    type="primary"
                    icon={<PlusOutlined />}
                    shape="circle"
                    ghost={true}
                    size="small"
                    onClick={() => {
                      setEditingField(null);
                      fieldForm.resetFields();
                      setIsFieldModalVisible(true);
                    }}
                  />
                </Tooltip>
              </div>
            )
          }
          <div
            className="pos-relative overflow-y"
            style={{ height: "calc( 100% - 50px)" }}
          >
            <div className="pb-4">{renderFieldList()}</div>
          </div>
        </Splitter.Panel>
      </Splitter>

      {/* / */}
      <Modal
        title={selectedSchema ? "" : ""}
        open={isSchemaModalVisible}
        onOk={() => schemaForm.submit()}
        onCancel={() => setIsSchemaModalVisible(false)}
      >
        <Form form={schemaForm} layout="vertical" onFinish={handleSchemaCreate}>
          <Form.Item
            name="name"
            label=""
            rules={[
              { required: true, message: "" },
              {
                pattern: /^[a-z][a-z0-9_]*$/,
                message:
                  "",
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="code"
            label=""
            rules={[
              { required: true, message: "" },
              {
                pattern: /^[a-zA-Z][a-zA-Z0-9_:]*$/,
                message: "",
              },
            ]}
          >
            <Input placeholder="system:user" />
          </Form.Item>
          <Form.Item name="description" label="">
            <Input.TextArea />
          </Form.Item>
          <Form.Item
            name="isActive"
            label=""
            valuePropName="checked"
            initialValue={true}
          >
            <Switch checkedChildren="" unCheckedChildren="" />
          </Form.Item>
        </Form>
      </Modal>

      {/* / */}
      <Modal
        title={editingField ? "" : ""}
        open={isFieldModalVisible}
        onOk={() => fieldForm.submit()}
        onCancel={() => {
          setIsFieldModalVisible(false);
          setFieldType("");
          fieldForm.resetFields();
        }}
        width={720}
      >
        <Form
          form={fieldForm}
          layout="horizontal"
          labelCol={{ span: 6 }}
          wrapperCol={{ span: 16 }}
          onFinish={(values: any) => {
            if (editingField) {
              const index =
                selectedSchema?.fields.findIndex(
                  (f) => f.id === editingField.id
                ) ?? -1;
              if (index !== -1) {
                handleFieldEdit(values, index);
              }
            } else {
              handleFieldCreate(values);
            }
          }}
        >
          <Form.Item
            name="type"
            label=""
            rules={[{ required: true, message: '' }]}
          >
            <Select onChange={(value: any) => {
              // 
              fieldForm.resetFields(['length', 'dateType', 'enumConfig', 'relationConfig', 'mediaConfig', 'apiConfig']);
              setFieldType(value);
              setNumberType('');
            }}>
              {fieldTypes.map(type => (
                <Option key={type.value} value={type.value}>{type.label}</Option>
              ))}
            </Select>
          </Form.Item>

          <Form.Item
            name="name"
            label=""
            rules={[{ validator: validateFieldName }]}
          >
            <Input placeholder="" />
          </Form.Item>

          <Form.Item
            name="description"
            label=""
          >
            <Input placeholder="" />
          </Form.Item>

          <Form.Item
            name="required"
            valuePropName="checked"
            label=""
          >
            <Switch />
          </Form.Item>

          {/* UUIDID -  */}
          {/* {(fieldType === 'uuid' || fieldType === 'auto_increment') && (
            <Form.Item
              name="isPrimaryKey"
              valuePropName="checked"
              label=""
            >
              <Switch />
            </Form.Item>
          )} */}

          {/* UUIDID */}
          {!['uuid', 'auto_increment'].includes(fieldType) && (
            <>
              {/*  */}
              {fieldType === 'string' && (
                <Form.Item
                  name="length"
                  label=""
                  rules={[{ required: true, message: '' }]}
                >
                  <InputNumber min={1} max={255} />
                </Form.Item>
              )}

              {/*  */}
              {fieldType === 'number' && (
                <>
                  <Form.Item
                    name="numberType"
                    label=""
                    rules={[{ required: true, message: '' }]}
                  >
                    <Select onChange={(value: any) => {
                      setNumberType(value);
                      if (value === 'integer') {
                        fieldForm.setFieldsValue({ precision: undefined, scale: undefined });
                      }
                    }}>
                      <Option value="integer"></Option>
                      <Option value="float"></Option>
                      <Option value="decimal"></Option>
                    </Select>
                  </Form.Item>
                  {(numberType === 'float' || numberType === 'decimal') && (
                    <>
                      <Form.Item
                        name="precision"
                        label=""
                        tooltip=""
                        rules={[{ required: true, message: '' }]}
                      >
                        <InputNumber min={1} max={65} />
                      </Form.Item>
                      <Form.Item
                        name="scale"
                        label=""
                        tooltip=""
                        rules={[
                          { required: true, message: '' },
                          ({ getFieldValue }) => ({
                            validator(_, value) {
                              const precision = getFieldValue('precision');
                              if (value > precision) {
                                return Promise.reject('');
                              }
                              return Promise.resolve();
                            },
                          }),
                        ]}
                      >
                        <InputNumber min={0} max={30} />
                      </Form.Item>
                    </>
                  )}
                </>
              )}

              {/*  */}
              {fieldType === 'date' && (
                <Form.Item
                  name="dateType"
                  label=""
                  rules={[{ required: true, message: '' }]}
                >
                  <Select>
                    <Option value="year"></Option>
                    <Option value="year-month"></Option>
                    <Option value="date"></Option>
                    <Option value="datetime"></Option>
                  </Select>
                </Form.Item>
              )}

              {/*  */}
              {fieldType === 'enum' && (
                <>
                  <Form.Item
                    name="targetEnumCode"
                    label=""
                    rules={[{ required: true, message: '' }]}
                  >
                    <div>
                      <span style={{ marginRight: 8 }}>{enumDisplayText || ''}</span>
                      <Cascader<EnumTreeNode>
                        options={enumTreeData}
                        onChange={handleEnumChange}
                        placeholder=""
                        showSearch={{ filter: enumFilter }}
                      >
                        <a></a>
                      </Cascader>
                    </div>
                  </Form.Item>
                  <Form.Item
                    name="multiple"
                    valuePropName="checked"
                    label=""
                  >
                    <Switch />
                  </Form.Item>
                </>
              )}

              {/*  */}
              {fieldType === 'relation' && (
                <>
                  <Form.Item
                    name="relationType"
                    label=""
                    rules={[{ required: true, message: '' }]}
                    tooltip={{
                      title: (
                        <div>
                          <p>-</p>
                          <p>-</p>
                          <p>-</p>
                          <p>-</p>
                        </div>
                      ),
                    }}
                  >
                    <Select>
                      <Option value="oneToOne"> (1:1)</Option>
                      <Option value="oneToMany"> (1:n)</Option>
                      <Option value="manyToOne"> (n:1)</Option>
                      <Option value="manyToMany"> (m:n)</Option>
                    </Select>
                  </Form.Item>

                  <Form.Item
                    name="targetSchema"
                    label=""
                    rules={[{ required: true, message: '' }]}
                    tooltip={{
                      title: ''
                    }}
                  >
                    <TreeSelect
                      treeData={processTreeData(schemaTreeData, selectedSchema?.code || '').map(node => ({
                        title: node.name,
                        value: node.code,
                        disabled: node.disabled,
                        children: node.children?.map(child => ({
                          title: child.name,
                          value: child.code,
                          disabled: child.disabled,
                          children: child.children?.map(grandChild => ({
                            title: grandChild.name,
                            value: grandChild.code,
                            disabled: grandChild.disabled,
                            isLeaf: !grandChild.children?.length
                          }))
                        }))
                      }))}
                      placeholder=""
                      allowClear
                      showSearch
                      treeNodeFilterProp="title"
                      filterTreeNode={treeFilter}
                      treeDefaultExpandAll
                      onChange={(value) => {
                        console.log(':', value);
                        // 
                        fieldForm.setFieldValue('targetField', undefined);
                      }}
                    />
                  </Form.Item>

                  <Form.Item
                    name="targetField"
                    label=""
                    tooltip={{
                      title: (
                        <div>
                          <p></p>
                          <p>- id </p>
                          <p>- </p>
                          <p>- </p>
                        </div>
                      ),
                    }}
                  >
                    <Select
                      placeholder=""
                      allowClear
                      showSearch
                    >
                      {schemas.find(s => s.code === fieldForm.getFieldValue('targetSchema'))?.fields.map(field => (
                        <Option 
                          key={field.name} 
                          value={field.name}
                          disabled={!isValidRelationField(field)}
                        >
                          {field.name}
                          {field.description ? ` (${field.description})` : ''}
                          {/* {isPrimaryKeyField(field) ? ' []' : ''} */}
                        </Option>
                      ))}
                    </Select>
                  </Form.Item>

                  <Form.Item
                    name="cascadeDelete"
                    label=""
                    rules={[{ required: true, message: '' }]}
                    tooltip={{
                      title: (
                        <div>
                          <p></p>
                          <p></p>
                          <p></p>
                        </div>
                      ),
                    }}
                  >
                    <Select>
                      <Option value="restrict"></Option>
                      <Option value="cascade"></Option>
                      <Option value="setNull"></Option>
                    </Select>
                  </Form.Item>
                </>
              )}

              {/*  */}
              {fieldType === 'media' && (
                <>
                  <Form.Item
                    name={['mediaConfig', 'mediaType']}
                    label=""
                    rules={[{ required: true, message: '' }]}
                  >
                    <Select>
                      <Option value="image"></Option>
                      <Option value="video"></Option>
                      <Option value="audio"></Option>
                      <Option value="document"></Option>
                      <Option value="file"></Option>
                    </Select>
                  </Form.Item>
                  <Form.Item
                    name={['mediaConfig', 'multiple']}
                    valuePropName="checked"
                    label=""
                  >
                    <Switch />
                  </Form.Item>
                  <Form.Item
                    name={['mediaConfig', 'maxSize']}
                    label="(MB)"
                  >
                    <InputNumber min={1} />
                  </Form.Item>
                  <Form.Item
                    name={['mediaConfig', 'formats']}
                    label=""
                  >
                    <Select mode="tags" placeholder=": jpg, png">
                      <Option value="jpg">jpg</Option>
                      <Option value="png">png</Option>
                      <Option value="pdf">pdf</Option>
                      <Option value="doc">doc</Option>
                      <Option value="docx">docx</Option>
                    </Select>
                  </Form.Item>
                </>
              )}

              {/* API */}
              {fieldType === 'api' && (
                <>
                  <Form.Item
                    name={['apiConfig', 'endpoint']}
                    label="API"
                    rules={[{ required: true, message: 'API' }]}
                  >
                    <Input placeholder="API" />
                  </Form.Item>
                  <Form.Item
                    name={['apiConfig', 'method']}
                    label=""
                    rules={[{ required: true, message: '' }]}
                  >
                    <Select>
                      <Option value="GET">GET</Option>
                      <Option value="POST">POST</Option>
                      <Option value="PUT">PUT</Option>
                      <Option value="DELETE">DELETE</Option>
                    </Select>
                  </Form.Item>
                  <Form.Item
                    name={['apiConfig', 'multiple']}
                    valuePropName="checked"
                    label=""
                  >
                    <Switch />
                  </Form.Item>
                </>
              )}
            </>
          )}
        </Form>
      </Modal>

      {/*  */}
      <Modal
        title=""
        open={isEnumModalVisible}
        onOk={() => {
          if (selectedEnumId) {
            fieldForm.setFieldValue("targetEnumCode", selectedEnumId);
            setIsEnumModalVisible(false);
          } else {
            message.warning("");
          }
        }}
        onCancel={() => {
          setIsEnumModalVisible(false);
          setEnumSearchValue("");
        }}
        width={720}
      >
        <Input.Search
          placeholder=""
          value={enumSearchValue}
          onChange={(e) => setEnumSearchValue(e.target.value)}
          style={{ marginBottom: 16 }}
        />
        <List
          dataSource={filteredEnums}
          size="small"
          renderItem={(item: any) => (
            <List.Item
              onClick={() => setSelectedEnumId(item.code)}
              style={{
                cursor: "pointer",
                backgroundColor:
                  selectedEnumId === item.code ? "#e6f7ff" : undefined,
              }}
            >
              <List.Item.Meta
                title={
                  <Space>
                    <span>{item.name}</span>
                    <Tag color="blue">{item.code}</Tag>
                    {!item.isActive && <Tag color="red"></Tag>}
                  </Space>
                }
                description={item.description}
              />
              <div>
                {item.options?.map((option: any) => (
                  <Tag key={option.value}>{option.label}</Tag>
                ))}
              </div>
            </List.Item>
          )}
        />
      </Modal>
    </div>
  );
};

export default SchemaManagement;

