                      expandedKeys={expandedKeys}
                      checkedKeys={checkedKeys}
                      // height={300}
                      onExpand={(expandedKeysValue) => {
                        setExpandedKeys(expandedKeysValue as string[]);
                      }}
                      onCheck={(checkedKeysValue) => {
                        const keys = Array.isArray(checkedKeysValue) ? checkedKeysValue : checkedKeysValue.checked;
                        setCheckedKeys(keys as string[]);
                        // code
                        const leafCodes: string[] = [];
                        const collectLeafCodes = (nodes: any[], checkedKeys: string[]) => {
                          nodes.forEach(node => {
                            const nodeKey = node.key || node.value;
                            if (checkedKeys.includes(nodeKey)) {
                              if (node.children && node.children.length > 0) {
                                collectLeafCodes(node.children, checkedKeys);
                              } else {
                                // 
                                if (node.rawSchema) {
                                  leafCodes.push(node.rawSchema.code);
                                }
                              }
                            }
                          });
                        };
                        collectLeafCodes(schemaTreeData, keys as string[]);
                        setSelectedSchemas(leafCodes);
                      }}
                      onSelect={(selectedKeys, info) => {
                        // 
                        if (info.node.rawSchema) {
                          setPreviewSchema(info.node.rawSchema);
                        } else {
                          setPreviewSchema(null);
                        }
                      }}
                      titleRender={(node) => (
                        <div>
                          <div style={{ fontWeight: 'bold' }}>{node.label}
                          {node.rawSchema && (
                            <span style={{ color: '#666', fontSize: '12px' }}>
                              : {node.rawSchema.fields?.length || 0}
                            </span>
                          )}
                          </div>
                        </div>
                      )}
                    />
                    {schemaTreeData.length === 0 && (
                      <div style={{ textAlign: 'center', color: '#999', padding: '20px' }}>

                      </div>
                    )}
                  </div>
                </div>
              </Splitter.Panel>
              <Splitter.Panel>
                <div style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                  {/* <div style={{ height: '32px', display: 'flex', alignItems: 'center' }}>
                    <h4 style={{ margin: 0 }}></h4>
                  </div> */}
                  <div style={{ 
                    flex: 1,
                    overflowY: 'auto',
                    borderRadius: '6px',
                  }}>
                    {renderFieldPreview()}
                  </div>
                </div>
              </Splitter.Panel>
            </Splitter>
          </div>

          <Form.Item
            name="targetSchema"
            label="Schema"
            rules={[{ required: true, message: 'Schema' }]}
          >
            <Input placeholder="public" />
          </Form.Item>

          <Form.Item
            name="tablePrefix"
            label=""
          >
            <Input placeholder="" />
          </Form.Item>

          <Form.Item
            name="overwrite"
            valuePropName="checked"
          >
            <Checkbox></Checkbox>
          </Form.Item>

          <Form.Item
            name="includeIndexes"
            valuePropName="checked"
          >
            <Checkbox></Checkbox>
          </Form.Item>

          <Form.Item
            name="includeConstraints"
            valuePropName="checked"
          >
            <Checkbox></Checkbox>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default DatabaseManagement;

