      // URL
      const png = cy.png({
        full: true,
        quality: 1,
        output: 'blob'
      });

      // 
      const url = URL.createObjectURL(png);
      const link = document.createElement('a');
      link.href = url;
      link.download = `schema-graph-${new Date().toISOString().slice(0, 10)}.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    }
  };

  const initCytoscape = async () => {
    console.log(' Cytoscape');

    //  ID 
    let container = document.getElementById('cytoscape-container');

    if (!container) {
      console.error('!');
      setError('');
      return;
    }

    console.log(':', container);
    console.log(':', container.offsetWidth, 'x', container.offsetHeight);

    try {
      // 
      console.log('...');
      const [schemasData, enumsData] = await Promise.all([
        getSchemas({ code: undefined, name: undefined }),
        getEnums({})
      ]);

      console.log(':', schemasData);
      console.log(':', enumsData);

      // 
      setSchemas(schemasData);
      setEnums(enumsData);

      //  cytoscape 
      const nodes: any[] = [];
      const edges: any[] = [];

      // 
      schemasData.forEach((schema) => {
        if (schema.code && schema.name) {
          const label = schema.description 
            ? `${schema.name}\n${schema.description}`
            : schema.name;
          nodes.push({
            data: {
              id: schema.code,
              label: label,
              type: 'schema',
              name: schema.name,
              description: schema.description
            }
          });
        }
      });

      // 
      enumsData.forEach((enumItem) => {
        if (enumItem.code) {
          const label = enumItem.description 
            ? `${enumItem.name}\n${enumItem.description}`
            : enumItem.name;
          nodes.push({
            data: {
              id: enumItem.code,
              label: label,
              type: 'enum',
              name: enumItem.name,
              description: enumItem.description
            }
          });
        }
      });

      // 
      schemasData.forEach((schema) => {
        schema.fields?.forEach((field) => {
          // 
          if (field.type === 'relation' && field.relationConfig?.targetSchemaCode && field.name && schema.code) {
            const targetSchema = schemasData.find((s) => s.code === field.relationConfig?.targetSchemaCode);
            if (targetSchema?.code) {
              const relationField = field as API.RelationField;
              const relationType = relationField.relationConfig?.multiple
                ? relationField.relationConfig?.targetSchemaCode === schema.code
                  ? "manyToMany"
                  : "oneToMany"
                : relationField.relationConfig?.targetSchemaCode === schema.code
                ? "manyToOne"
                : "oneToOne";

              edges.push({
                data: {
                  id: `${schema.code}-${targetSchema.code}-${field.name}`,
                  source: schema.code,
                  target: targetSchema.code,
                  label: `${field.name} (${relationType})`,
                  type: 'relation',
                  relationType: relationType
                }
              });
            }
          }
          // 
          else if (field.type === 'enum' && field.enumConfig?.targetEnumCode && schema.code) {
            const enumField = field as API.EnumField;
            const enumSchema = enumsData.find((e) => e.code === enumField.enumConfig?.targetEnumCode);

            if (enumSchema && enumSchema.code) {
              edges.push({
                data: {
                  id: `${schema.code}-${enumSchema.code}-${field.name}`,
                  source: schema.code,
                  target: enumSchema.code,
                  label: `${field.name} (${enumField.enumConfig?.multiple ? '' : ''})`,
                  type: 'enum',
                  multiple: enumField.enumConfig?.multiple
                }
              });
            }
          }
        });
      });

      const graphData = { nodes, edges };
      console.log(':', graphData);

      console.log(' Cytoscape ...');
      const cy = cytoscape({
        container: container,
        elements: graphData,
        style: [
          {
            selector: 'node',
            style: {
              'background-color': 'data(type)',
              'label': 'data(label)',
              'color': '#fff',
              'width': 'data(type)',
              'height': 'data(type)',
              'font-size': '13px',
              'text-valign': 'center',
              'text-halign': 'center',
              'text-wrap': 'wrap',
              'text-max-width': '80px',
              'border-width': 0
            }
          },
          {
            selector: 'node[type = "schema"]',
            style: {
              'background-color': '#1890ff',
              'width': '100px',
              'height': '100px',
              'font-size': '16px',
              'text-max-width': '90px'
            }
          },
          {
            selector: 'node[type = "enum"]',
            style: {
              'background-color': '#52c41a',
              'width': '80px',
              'height': '80px',
              'font-size': '14px',
              'text-max-width': '70px'
            }
          },
          {
            selector: 'edge',
            style: {
              'width': 2,
              'line-color': '#fff',
              'target-arrow-color': '#fff',
              'target-arrow-shape': 'triangle',
              'curve-style': 'bezier',
              'label': 'data(label)',
              'color': '#fff',
              'font-size': '10px',
              'text-rotation': 'autorotate',
              'text-margin-y': -10
            }
          },
          {
            selector: 'edge[type = "relation"]',
            style: {
              'line-color': '#1890ff',
              'target-arrow-color': '#1890ff',
              'width': 3
            }
          },
          {
            selector: 'edge[type = "enum"]',
            style: {
              'line-color': '#52c41a',
              'target-arrow-color': '#52c41a',
              'width': 2,
              'line-style': 'dashed'
            }
          }
        ],
        layout: getLayoutConfig('cola')
      });

      //  cytoscape 
      cyRef.current = cy;

      console.log('Cytoscape :', cy);
      console.log(':', cy.nodes().length);
      console.log(':', cy.edges().length);

      // 
      cy.on('tap', 'node', function(evt) {
        const node = evt.target;
        console.log(':', node.data());
        // 
        showNodeDetailsWithData(node.data(), schemasData, enumsData);
      });

      cy.on('tap', 'edge', function(evt) {
        const edge = evt.target;
        console.log(':', edge.data());
        showEdgeDetails(edge.data());
      });

      // 
      cy.fit();
      console.log(' cy.fit()');

      // 
      cy.resize();
      console.log(' cy.resize()');

    } catch (error) {
      console.error('Cytoscape :', error);
      setError('Cytoscape : ' + error);
    }
  };

  return (
    <>
      <div style={{ width: "100%", height: "calc(100vh - 55px)", background: "#000" }}>
        {loading ? (
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            height: '100%',
            background: '#000',
            color: '#fff',
            fontSize: '20px'
          }}>
            ...
          </div>
        ) : (
          <div 
            id="cytoscape-container"
            style={{ 
              width: '100%', 
              height: '100%',
              background: '#000'
            }}
          />
        )}
      </div>

      <Button 
        type="primary"
        ghost
        className="position-absolute"
        style={{ top: "10px", left: "10px", zIndex: 1000 }}
        icon={<RollbackOutlined />}
        onClick={() => {
          history.back();
        }}
      >

      </Button>

      {/*  */}
      <Card
        size="small"
        className="position-absolute"
        style={{ 
          top: "10px", 
          right: "10px", 
          zIndex: 1000,
          background: '#fafafa0a',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.2)'
        }}
      >
        <Space size="small">
          <Checkbox 
            checked={hideEnums}
            onChange={(e: any) => handleHideEnumsChange(e.target.checked)}
          >

          </Checkbox>

          <Button 
            size="small"
            icon={<OneToOneOutlined />}
            onClick={handleResetTo1To1}
          >
            1:1
          </Button>

          <Button 
            size="small"
            icon={<GatewayOutlined />}
            onClick={handleFitView}
          />

          {/* <Select
            size="small"
            style={{ width: 120 }}
            value={currentLayout}
            onChange={handleLayoutChange}
            options={layoutOptions}
            placeholder=""
          /> */}

          <Button 
            size="small"
            type="primary"
            icon={<DownloadOutlined />}
            onClick={handleExportImage}
          >

          </Button>
        </Space>
      </Card>

      <Drawer
        title={drawerTitle}
        placement="right"
        width={600}
        onClose={() => setDrawerVisible(false)}
        open={drawerVisible}
      >
        {drawerContent && (
          <>
            {drawerContent.type === 'schema' && renderSchemaDetails(drawerContent.data)}
            {drawerContent.type === 'enum' && renderEnumDetails(drawerContent.data)}
            {drawerContent.type === 'edge' && renderEdgeDetails(drawerContent.data)}
          </>
        )}
      </Drawer>
    </>
  );
};

export default SchemaGraphNew2;
